mapper.select:
SELECT o.*,u.trueName AS "stafName" FROM sys_org o LEFT JOIN sys_user u ON o.stafId = u.id WHERE 0=0
AND (o.code LIKE "%"#{searchField}"%" OR u.trueName LIKE "%"#{searchField}"%" OR o.name LIKE "%"#{searchField}"%")
AND stafId = #{stafId}

SELECT u.id, u.username, u.password, u.sex, u.phone, u.trueName, u.idCard, u.address, u.orgId, u.state, u.type, u.hireDate, u.imgIdcard1, u.imgIdcard2, u.createTime, u.updateTime,r.id AS "roleId", r.roleName AS "role" FROM sys_user u LEFT JOIN sys_user_role ur ON ur.userId=u.id LEFT JOIN sys_role r ON r.id=ur.roleId WHERE u.state = 0 AND r.isDel=0 AND (r.roleName='业务员')
AND u.orgId = #{orgId}
AND u.trueName LIKE "%"#{trueName}"%"
